<?phpnamespace DnD\Bundle\MagentoConnectorBundle\DependencyInjection;use Symfony\Component\HttpKernel\DependencyInjection\Extension;use Symfony\Component\DependencyInjection\ContainerBuilder;use Symfony\Component\DependencyInjection\Loader;use Symfony\Component\Config\FileLocator;/** * * @author    DnD Mimosa <mimosa@dnd.fr> * @copyright Agence Dn'D (http://www.dnd.fr) * @license   http://opensource.org/licenses/osl-3.0.php  Open Software License (OSL 3.0) */class DnDMagentoConnectorExtension extends Extension{    /**     * {@inheritdoc}     */    public function load(array $configs, ContainerBuilder $container)    {        $loader = new Loader\YamlFileLoader($container, new FileLocator(__DIR__.'/../Resources/config'));        $loader->load('readers.yml');        $loader->load('processors.yml');        $loader->load('writers.yml');        $storageDriver = $container->getParameter('pim_catalog.storage_driver');        $storageConfig = sprintf('storage_driver/%s.yml', $storageDriver);        if (file_exists(__DIR__ . '/../Resources/config/' . $storageConfig)) {            $loader->load($storageConfig);        }    }}