<?phpnamespace DnD\Bundle\MagentoConnectorBundle\Processor;use Symfony\Component\Validator\Constraints as Assert;use Pim\Bundle\BaseConnectorBundle\Validator\Constraints\Channel as ChannelConstraint;use Pim\Bundle\CatalogBundle\Manager\ChannelManager;use Akeneo\Bundle\BatchBundle\Item\ItemProcessorInterface;use Akeneo\Bundle\BatchBundle\Item\AbstractConfigurableStepElement;/** * * @author    Mimosa <mimosa@dnd.fr> * @copyright 2014 Agence Dn'D (http://www.dnd.fr) * @license   http://www.dnd.fr */ class FamilyProcessor extends AbstractConfigurableStepElement implements ItemProcessorInterface{		/**     * {@inheritdoc}     */    public function process($family)    {	            $result = [];	            $result['code']    = $family->getCode();        $result['label']   = $family->setLocale('fr_FR')->getLabel();		        return $result;    }        /**     * {@inheritdoc}     */    public function getConfigurationFields()    {        return array();    }    }