<?phpnamespace DnD\Bundle\MagentoConnectorBundle\Processor;use Symfony\Component\Validator\Constraints as Assert;use Pim\Bundle\BaseConnectorBundle\Validator\Constraints\Channel as ChannelConstraint;use Pim\Bundle\CatalogBundle\Manager\ChannelManager;use Akeneo\Bundle\BatchBundle\Item\ItemProcessorInterface;use Akeneo\Bundle\BatchBundle\Item\AbstractConfigurableStepElement;/** * * @author    DnD Mimosa <mimosa@dnd.fr> * @copyright Agence Dn'D (http://www.dnd.fr) * @license   http://opensource.org/licenses/osl-3.0.php  Open Software License (OSL 3.0) */ class FamilyProcessor extends AbstractConfigurableStepElement implements ItemProcessorInterface{    /** @var string */    protected $locale = 'en_US';    /**     * Set locale     * @param string $locale     */    public function setLocale($locale)    {        $this->locale = $locale;    }    /**     * Get locale     * @return string     */    public function getLocale()    {        return $this->locale;    }		/**     * {@inheritdoc}     */    public function process($family)    {	            $result = [];	            $result['code']    = $family->getCode();        $result['label']   = $family->setLocale($this->getLocale())->getLabel();		        return $result;    }        /**     * {@inheritdoc}     */    public function getConfigurationFields()    {        return array(            'locale' => array(                'type'    => 'text',                'options' => array(                    'required' => true,                    'label'    => 'dnd_magento_connector.export.locale.label',                    'help'     => 'dnd_magento_connector.export.locale.help',                )            )        );    }    }